export function seededRandom(seed) {
    let s = seed;
    return function () {
        s = (s * 16807 + 0) % 2147483647;
        return (s - 1) / 2147483646;
    };
}

export function shuffleArray(array, seed) {
    const arr = [...array];
    const rng = seededRandom(seed);
    for (let i = arr.length - 1; i > 0; i--) {
        const j = Math.floor(rng() * (i + 1));
        [arr[i], arr[j]] = [arr[j], arr[i]];
    }
    return arr;
}

export function decodeBoardId(id) {
    const seed = parseInt(id.slice(0, 6), 36);
    const theme = THEME_KEYS[parseInt(id[6], 10)];
    const cellsNum = parseInt(id.slice(7, 12), 36);
    const bits = cellsNum.toString(2).padStart(25, '0');
    const cells = bits.split('').map(b => b === '1');
    return { seed, theme, cells };
}

export function getTitle(checked) {
    let title = titleTiers[0][1];
    for (const [min, name] of titleTiers) {
        if (checked >= min) title = name;
    }
    return title;
}

export function reconstructBoard(boardId) {
    const { seed, theme, cells } = decodeBoardId(boardId);
    const prompts = themePrompts[theme] || themePrompts.classic;
    const shuffled = shuffleArray(prompts, seed).slice(0, 24);
    const checked = cells.filter((v, i) => v && i !== 12).length;
    const title = getTitle(checked);
    return { seed, theme, cells, shuffled, checked, title };
}
